css:
	.print-format table, .print-format tr, 
	.print-format td, .print-format div, .print-format p {
		vertical-align: middle;
	}
	@media screen {
		.print-format {
			/*width: 5.8in;*/
			padding: 0.80in;
			/*height: 8.3in;*/
			margin: 0.5in;
		}
		body{
	        font-size:5px;
	    }
	    div.divFooter {
    	display: none;
      }
	}
	@media print {
		.print-format {
			/*width: 5.8in;*/
			padding: 0.40in;
			/*height: 8.3in;*/
			marign:0.3in;
		}
		body{
	        font-size:5px;
	        zoom: 80%;
	    }
	    div.divFooter {
            position: fixed;
            bottom: 0;
            padding-bottom: 5px;
        }
	    .table-header {
    	    background-color: #080808 !important;
    	    color: #000000 !important;
	    }
	    .th-text {
	        color: black !important;
	    }
	}
	.table-header {
	    background: grey;
	    color: #000000;
	}
	.th-text {
	    /*color: white !important;*/
	}
	.text-lg{
	    font-size:25px;
	    font-weight: bold;
	}
	.text-md{
	    margin-left:0px;
	    font-size:20px;
	}
	.text-sm{
        line-height: 14px;
        font-size:12px;
	}
	.text-sm2{
        line-height: 14px !important;
        font-size:12px !important;
	}

	.print-format td, .print-format th {
        vertical-align: top !important;
        padding: 4px !important;
        font-size:10px;
    }

    .vertical-center {
        margin: 0;
        position: absolute;
        top: 50%;
        -ms-transform: translateY(-50%);
        transform: translateY(-50%);
    }
    .ml-12{
        margin-left:-12px;
    }
    .footer-banner{
    	background: #d2d3d9;
	    color: #000000;
	    width: 100%;
    }

    .print-format, .div-footer {
        padding: .3in;
    }
    .row_amount{
    	font-weight: bold !important;
    	margin-top: 7px;
    }
